IndicadorTemplate.create(columna: 3, fila: 2, has_na: false, item_template_id: 12, descripcion: 'La sala está razonablemente silenciosa de manera que los niños puedan oír el lenguaje.', tooltip: '')
IndicadorTemplate.create(columna: 3, fila: 3, has_na: false, item_template_id: 12, descripcion: 'El personal por lo general habla con los niños en un tono de voz neutro y agradable.', tooltip: '')
IndicadorTemplate.create(columna: 3, fila: 4, has_na: false, item_template_id: 12, descripcion: 'El contenido de la conversación es generalmente alentador y positivo más que desalentador y negativo.', tooltip: 'El 75% de la conversación debe ser alentador y el resto neutral')
IndicadorTemplate.create(columna: 5, fila: 1, has_na: false, item_template_id: 12, descripcion: 'El personal habla con los niños frecuentemente a lo largo del día durante las rutinas y los juegos.', tooltip: 'Aunque la cantidad de conversación de los distintos miembros del personal puede variar todo el personal debe usar un tono de voz neutro o agradable.')
IndicadorTemplate.create(columna: 5, fila: 2, has_na: false, item_template_id: 12, descripcion: 'La conversación del personal tiene sentido para los niños.', tooltip: 'Por ej. hablan acerca de cosas que los niños sienten o hacen; usan oraciones sencillas que los niños pueden entender; usan gestos para añadir significado a las palabras.')
IndicadorTemplate.create(columna: 5, fila: 3, has_na: false, item_template_id: 12, descripcion: 'La comunicación verbal es personalizada.', tooltip: 'Por ej. se mira a los niños a los ojos; se llama a los niños por su nombre; se les haba en su idioma primario; se usan señales u otro tipo de comunicación si es necesario.')
IndicadorTemplate.create(columna: 5, fila: 4, has_na: false, item_template_id: 12, descripcion: 'El personal por lo general usa palabras sencillas y descriptiva para los objetos y acciones cuando se comunica con los niños.', tooltip: 'Para determinar si el lenguaje es descriptivo pregúntese si usted entiende el tema que el personal habla con los niños con sólo escuchar y sin mirar.')
IndicadorTemplate.create(columna: 7, fila: 1, has_na: false, item_template_id: 12, descripcion: 'El personal usa una amplia variedad de palabras sencillas y exactas para comunicarse con los niños.', tooltip: 'Por ej. nombra muchos objetos y acciones distintas; usa palabras descriptivas.')
IndicadorTemplate.create(columna: 7, fila: 2, has_na: false, item_template_id: 12, descripcion: 'El personal participa en el juego verbal con los niños.', tooltip: 'Se observa al personal participando al menos 2 ejemplos.   /   Por ej. repite los sonidos del bebé; rima las palabras de manera juguetona.')
IndicadorTemplate.create(columna: 7, fila: 3, has_na: false, item_template_id: 12, descripcion: 'El personal habla acerca de muchos temas distintos con los niños.', tooltip: 'Por ej. habla acerca de los sentimientos; expresa las intenciones del niño con palabras además de nombra objetos y acciones.')

# Indicadores Item 2, Sub-escala 3, Escala ITERS
IndicadorTemplate.create(columna: 1, fila: 1, has_na: false, item_template_id: 13, descripcion: 'Hay pocas o ninguna respuesta positiva  a los intentos de los niños de comunicarse por medios de gestos sonidos o palabras.', tooltip: '')
IndicadorTemplate.create(columna: 1, fila: 2, has_na: false, item_template_id: 13, descripcion: 'El personal a menudo ignora los intentos de comunicación de los niños o responde negativamente.', tooltip: '')
IndicadorTemplate.create(columna: 3, fila: 1, has_na: false, item_template_id: 13, descripcion: 'Hay una medida moderada de respuesta positiva verbal y no verbal a los intentos de los niños de comunicación durante el día; se ignora o se dan respuestas negativas a los niños en poca o ninguna medida.', tooltip: 'NO pueden haber respuestas negativas')
IndicadorTemplate.create(columna: 3, fila: 2, has_na: false, item_template_id: 13, descripcion: 'Hay algunos intentos de interpretar correctamente lo que los niños están intentando comunicar durante el día.', tooltip: 'Por ej. el personal intenta usar otra manera de calmar a un niño que está llorando si la primera solución no funciona; el personal hace esfuerzos para entender las palabras poco claras de los niños pequeños.')
IndicadorTemplate.create(columna: 5, fila: 1, has_na: false, item_template_id: 13, descripcion: 'El personal en general responde de manera oportuna y positiva a los intentos de comunicación de los niños.', tooltip: 'Por ej. se atiende rápidamente a niño que llora; se atienden las solicitudes verbales de los niños; se responde con interés a las comunicaciones de los niños durante el juego.   /   Vea si el personal presta atención de cerca y responde a todos los niños del grupo. Incluso a aquellos que exigen menos atención que los otros.')
IndicadorTemplate.create(columna: 5, fila: 2, has_na: false, item_template_id: 13, descripcion: 'El personal agrega palabras a las acciones que lleva a cabo al responder a los niños durante el día.', tooltip: 'Durante el día: en actividades  y  rutina. VARIAS VECES   /   Por ej. "Estoy cambiándote el pañal. Ahora estás bien seco. Te sientes bien ¿no?".')
IndicadorTemplate.create(columna: 5, fila: 3, has_na: false, item_template_id: 13, descripcion: 'El personal es hábil en cuanto a interpretar los intentos de comunicación de los niños y hace seguimiento apropiadamente.', tooltip: 'Por ej. "Sé que tienes hambre; vamos a comer algo." "¿Estás cansado de jugar con esos bloques? Mira estos libros. ¿No? Ah quieres que te tome en brazos."   /   Interpreta correctamente más del 75%')
IndicadorTemplate.create(columna: 7, fila: 1, has_na: false, item_template_id: 13, descripcion: 'El personal tiene muchas conversaciones bidireccionales con los niños.', tooltip: 'Por ej. imitan alternativamente los sonidos de los bebés para hacer una "conversación de bebé"; repiten lo que el niño pequeño dice y luego deja que el niño pequeño diga algo.   /    Conversaciones durante rutina Y actividades')
IndicadorTemplate.create(columna: 7, fila: 2, has_na: true, item_template_id: 13, descripcion: 'El personal agrega palabras e ideas a las ideas de los niños.', tooltip: 'NA = Ninguno habla (ni siquiera palabras)   /   Observar al menos 2 ejemplos   /   Por ej. cuando el niño dice "jugo" el personal dice "Aquí tienes el jugo de naranja. Está en tu vaso".')
IndicadorTemplate.create(columna: 7, fila: 3, has_na: false, item_template_id: 13, descripcion: 'El personal le hace preguntas simples a los niños. ', tooltip: 'Por ej. hace una pregunta al bebé y luego da la respuesta: ¿Qué hay en esta foto? Un perro con un hueso." ; el personal espera que los niños pequeños contesten antes de dar la respuesta.   _/   Observar al menos 2 ejemplos')
IndicadorTemplate.create(columna: 7, fila: 4, has_na: false, item_template_id: 13, descripcion: 'El personal por lo general mantiene un buen equilibrio entre escuchar y hablar.', tooltip: 'Por ej. el personal le da al niño el tiempo necesario para que procese la información y responda; el personal habla más a los bebés que a los niños pequeños y le da más tiempo para que hablen a los niños pequeños que a los bebés.')

# Indicadores Item 3, Sub-escala 3, Escala ITERS
IndicadorTemplate.create(columna: 1, fila: 1, has_na: false, item_template_id: 14, descripcion: 'Menos de seis (6) libros apropiados para bebés y/o  niños pequeños accesibles diariamente durante gran parte del día.', tooltip: 'libros apropiados: libros resistentes de vinilo tela o páginas duras con imágenes adecuadas para bebés y niños pequeños. Los libros pueden ser hechos en casa o producidos comercialmente. Los libros para niños mayores o para adultos no cuentan para este ítem.')
IndicadorTemplate.create(columna: 1, fila: 2, has_na: false, item_template_id: 14, descripcion: 'Libros generalmente en mal estado.', tooltip: 'Por ejemplo libros rotos o incompletos; imágenes regadas; libros con garabatos.    /   Más del 50% en mal estado')
IndicadorTemplate.create(columna: 1, fila: 3, has_na: false, item_template_id: 14, descripcion: 'Los miembros del personal no les leen libros a los niños.', tooltip: 'Leer al menos 3 veces a la semana')
IndicadorTemplate.create(columna: 3, fila: 1, has_na: false, item_template_id: 14, descripcion: 'Como mínimo 6 (6) libros apropiados para bebés y/o niños pequeños (pero no menos de un (1) para cada niño en el grupo) accesibles diariamente durante gran parte del día.', tooltip: 'NO menos de 1 por niño en BUEN estado   /   Cuente sólo los libros completos con sus tapas y todas las páginas para dar una calificación positiva en el indicador. Los libros no apropiados para los niños del grupo (p. ej. demasiado difíciles demasiado fáciles aterradores o violentos) no pueden contarse entre los seis (6) libros exigidos')
IndicadorTemplate.create(columna: 3, fila: 2, has_na: false, item_template_id: 14, descripcion: 'Casi todos los libros están en buen estado.', tooltip: 'Sólo 3 libros pueden estar en mal estado   /   "En buen estado" significa que el libro tiene las tapas intactas no le faltan páginas y las páginas no están rasgadas o garabateadas. Los problemas menores tales como pequeños rasgones algunos garabatos marcas de mordidas etc. que no interfieren con el uso son aceptables.')
IndicadorTemplate.create(columna: 3, fila: 3, has_na: false, item_template_id: 14, descripcion: 'Los miembros del personal les leen libros a los niños diariamente (actividad iniciada por el personal o por los niños).', tooltip: 'Se debe OBSERVAR que les lean a los niños')
IndicadorTemplate.create(columna: 3, fila: 4, has_na: false, item_template_id: 14, descripcion: 'Se estimula la participación sólo mientas los niños están interesados; no se les obliga a participar.', tooltip: 'No se les obliga = pueden hacer otra cosa')
IndicadorTemplate.create(columna: 5, fila: 1, has_na: false, item_template_id: 14, descripcion: 'Como mínimo doce (12) libros apropiados para bebés y/o niños pequeños (pero no menos de dos (2) para cada niño en el grupo accesibles diariamente durante gran parte del día.', tooltip: 'Para dar una calificación positiva ningún libro puede ser violento ni aterrador')
IndicadorTemplate.create(columna: 5, fila: 2, has_na: false, item_template_id: 14, descripcion: 'Hay una gran selección de libros accesibles.', tooltip: ' ningún libro puede ser violento ni aterrador debe contener libros acerca de animales; objetos familiares; rutinas conocidas y personas de diversas razas edades y destrezas.')
IndicadorTemplate.create(columna: 5, fila: 3, has_na: false, item_template_id: 14, descripcion: 'El personal lee libros diariamente a niños solos o a grupos pequeños de niños interesados.', tooltip: 'Se debe OBSERVAR al menos una vez que lean en grupos pequeños.')
IndicadorTemplate.create(columna: 5, fila: 4, has_na: false, item_template_id: 14, descripcion: 'Las horas de lectura son cordiales e interactivas.', tooltip: 'Por ej. sostener al bebé mientras se lee el libro; dejar que el niño pequeño voltee las páginas  y señale las imágenes.   /   Esto se debe observar para dar una calificación positiva.')
IndicadorTemplate.create(columna: 7, fila: 1, has_na: true, item_template_id: 14, descripcion: 'El área de lectura está arreglada para que los niños pequeños la usen solos.', tooltip: 'En el área de lectura NO puede haber mucho material o juguetes que distraigan.   /   NA = si ninguno camina')
